#include <stdio.h> int main() { int v1 = 666; int *v2 = &v1; // & 取地址符，得到是 指针类型（64位操作系统，8字节） printf("v2的值为：%p
", v2); printf("v2解引用后：%d
",*v2); // 修改v1的值，查看v2 v1 = 999; printf("v1的值为：%p
", v1); printf("v2的值为：%p
", v2); printf("v2解引用后：%d
",*v2); return 0; }

#include <stdio.h> void changPointA(int *p) { *p = 99; } void changA(int a) { a = 99; } int main() { int a = 10; int *v2 = &a; printf("v2的值为:%p
", v2); // changPointA(v2); //传递到函数中，a的值被改变了 changA(a); //传递到函数中，a的值没有被改变 printf("v2的值为:%p
", v2); printf("a的值为:%d
", a); return 0; }

#include <stdio.h> int main() { int a = 100; int *p1 = &a; int **p2 = &p1; int ***p3 = &p2; printf("p3的值为:%p
", p3); printf("p2的值为:%p
", *p3); printf("p1的值为:%p
", **p3); printf("a的值为:%d
", ***p3); return 0; }

#include <stdio.h> #include <string.h> #include<stdlib.h> // 定义结构体 struct Person { char name[30]; int age; }; int main() { // 使用结构体 struct Person p1 = {"justin", 18}; struct Person p2 = {"彭于晏", 18}; struct Person p3 = {"古天乐", 18}; printf("p1的名字为：%s
", p1.name); printf("p2的名字为：%s
", p2.name); printf("p3的年龄为：%d
", p1.age); // 结构体指针 struct Person *pointPerson =&p3; printf("p3的年龄为：%d
", (*pointPerson).age); printf("p3的姓名为：%s
", pointPerson->name); return 0; }

#include <stdio.h> struct Node { int data; struct Node *next; }; int main() { struct Node v3 = {33}; struct Node v2 = {22, &v3}; struct Node v1 = {11, &v2}; printf("v1的数据为：%d
", v1.data); // 11 printf("v1的下一个元素数据为：%d
", v1.next->data); // 22 printf("v1的下一个元素的下一个数据为：%d
", v1.next->next->data); // 33 return 0; }
